mapper.select:
SELECT COUNT(*) as cnt FROM board
WHERE commgrpno=#{commgrpno}
WHERE commgrpno=#{commgrpno} AND (title LIKE '%' || #{word} || '%' OR bcon LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%' )

SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer r FROM ( SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer, rownum as r FROM ( SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer FROM board
WHERE commgrpno=#{commgrpno}
WHERE commgrpno=#{commgrpno} AND (title LIKE '%' || #{word} || '%' OR bcon LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%' )
ORDER BY boardno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer r FROM ( SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer, rownum as r FROM ( SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer FROM board
WHERE commgrpno=#{commgrpno}
WHERE commgrpno=#{commgrpno} AND (title LIKE '%' || #{word} || '%' OR bcon LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%' )
ORDER BY boardno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer r FROM ( SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer, rownum as r FROM ( SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer FROM board
WHERE commgrpno=#{commgrpno}
WHERE commgrpno=#{commgrpno} AND (title LIKE '%' || #{word} || '%' OR bcon LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%' )
ORDER BY boardno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

 SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer r
FROM (
           SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer, rownum as r
           FROM (
                     SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer
                     FROM board
                     WHERE commgrpno=#{commgrpno}
                     ORDER BY boardno DESC
                )
 )
 WHERE  r >= #{start_num} AND r <= #{end_num}

SELECT boardno, commgrpno, title, bcon, brdate, budate, word, file1, thumb, bsize, bcnt, writer
FROM board
WHERE boardno = #{boardno}

mapper.update:
UPDATE board
SET title=#{title}, bcon=#{bcon}, word=#{word}, budate=sysdate, file1=#{file1}, thumb=#{thumb}, bsize=#{bsize}
WHERE boardno = #{boardno}
